mapper.select:
SELECT count(*) 
FROM users
WHERE delete_state = #{deleteState}

SELECT
	COUNT(*)
FROM 
	users
WHERE 
	user_id = #{userId}
AND
	user_name = #{userName}
AND
	user_ssn1 = #{userSsn1}
AND 
	user_ssn2 = #{userSsn2}

mapper.update:
UPDATE 
	users
SET
	user_password = #{userPassword}
WHERE
	user_id = #{userId}
